// Generate hotspots based on report density
export const generateHotspots = (reports = sampleHazardReports) => {
  const hotspots = [];
  const locationGroups = {};

  // Group reports by proximity (within ~50km)
  reports.forEach(report => {
    const key = `${Math.round(report.location.latitude * 10) / 10}_${Math.round(report.location.longitude * 10) / 10}`;
    if (!locationGroups[key]) {
      locationGroups[key] = [];
    }
    locationGroups[key].push(report);
  });

  // Create hotspots from grouped locations
  Object.values(locationGroups).forEach(group => {
    if (group.length >= 2) { // At least 2 reports to form a hotspot
      const avgLat = group.reduce((sum, r) => sum + r.location.latitude, 0) / group.length;
      const avgLng = group.reduce((sum, r) => sum + r.location.longitude, 0) / group.length;

      const criticalCount = group.filter(r => r.severity === 'critical').length;
      const highCount = group.filter(r => r.severity === 'high').length;
      const activeCount = group.filter(r => r.status === 'active').length;

      let intensity = 'low';
      if (criticalCount > 0 || highCount >= 2) intensity = 'high';
      else if (highCount > 0 || activeCount >= 3) intensity = 'medium';

      hotspots.push({
        id: `hotspot_${Math.random().toString(36).substr(2, 9)}`,
        center: [avgLat, avgLng],
        intensity,
        reportCount: group.length,
        radius: Math.min(50000, group.length * 15000), // Max 50km radius
        reports: group.map(r => r.id),
        dominantTypes: [...new Set(group.map(r => r.type))],
        lastUpdated: new Date().toISOString()
      });
    }
  });

  return hotspots;
};

// Statistics for dashboard
export const getReportStatistics = (reports = sampleHazardReports) => {
  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  const thisWeek = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
  const thisMonth = new Date(today.getTime() - 30 * 24 * 60 * 60 * 1000);

  return {
    total: reports.length,
    active: reports.filter(r => r.status === 'active').length,
    critical: reports.filter(r => r.severity === 'critical').length,
    today: reports.filter(r => new Date(r.reportedAt) >= today).length,
    thisWeek: reports.filter(r => new Date(r.reportedAt) >= thisWeek).length,
    thisMonth: reports.filter(r => new Date(r.reportedAt) >= thisMonth).length,
    byType: reports.reduce((acc, r) => {
      acc[r.type] = (acc[r.type] || 0) + 1;
      return acc;
    }, {}),
    bySeverity: reports.reduce((acc, r) => {
      acc[r.severity] = (acc[r.severity] || 0) + 1;
      return acc;
    }, {}),
    byState: reports.reduce((acc, r) => {
      acc[r.location.state] = (acc[r.location.state] || 0) + 1;
      return acc;
    }, {})
  };
};

src/pages/Settings.css.content:
.settings-page {
  min-height: 100vh;
  background: linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%);
  padding: 2rem 1rem;
}

.settings-header {
  text-align: center;
  margin-bottom: 2rem;
}

.settings-header h1 {
  font-size: 2rem;
  color: #1f2937;
  margin-bottom: 0.5rem;
}

.settings-header p {
  color: #6b7280;
  font-size: 1.1rem;
}

.settings-container {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 300px 1fr;
  gap: 2rem;
  background: white;
  border-radius: 16px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  overflow: hidden;
}

.settings-sidebar {
  background: #f8fafc;
  padding: 2rem;
  border-right: 1px solid #e5e7eb;
}

.user-info {
  text-align: center;
  margin-bottom: 2rem;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid #e5e7eb;
}

.user-avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  font-weight: 700;
  margin: 0 auto 1rem;
}

.user-details h3 {
  color: #1f2937;
  margin-bottom: 0.5rem;
  font-size: 1.1rem;
}

.user-details p {
  color: #6b7280;
  text-transform: capitalize;
}

.settings-nav {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 2rem;
}

.nav-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem 1rem;
  background: transparent;
  border: none;
  border-radius: 8px;
  color: #6b7280;
  font-size: 0.95rem;
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: left;
}

.nav-item:hover {
  background: #e5e7eb;
  color: #374151;
}

.nav-item.active {
  background: #3b82f6;
  color: white;
}

.nav-icon {
  font-size: 1.1rem;
}

.settings-actions {
  padding-top: 1.5rem;
  border-top: 1px solid #e5e7eb;
}

.logout-btn {
  width: 100%;
  padding: 0.75rem;
  background: #ef4444;
  color: white;
  border: none;
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  transition: background 0.3s ease;
}

.logout-btn:hover {
  background: #dc2626;
}

.settings-content {
  padding: 2rem;
}

.tab-content h3 {
  color: #1f2937;
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  font-weight: 600;
}

.form-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
}

.form-group {
  display: flex;
  flex-direction: column;
}

.form-group label {
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
}

.form-group input,
.form-group select {
  padding: 0.75rem;
  border: 2px solid #d1d5db;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.3s ease;
}

.form-group input:focus,
.form-group select:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.notification-settings,
.checkbox-settings {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.notification-item,
.checkbox-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem;
  background: #f8fafc;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  cursor: pointer;
  transition: background 0.3s ease;
}

.notification-item:hover,
.checkbox-item:hover {
  background: #f1f5f9;
}

.notification-label {
  font-weight: 500;
